'',
'  FUNCTION GET_JAVASCRIPT_DATE_FORMAT (',
'    P_FORMAT IN VARCHAR2 )',
'    RETURN VARCHAR2',
'  IS',
'    L_MONTH_FORMAT       VARCHAR2(2)     := ''mm'';',
'    L_DAY_FORMAT         VARCHAR2(2)     := ''dd'';',
'    L_DAY_OF_YEAR_FORMAT VARCHAR2(2)     := ''oo'';',
'    L_ITEM_FORMAT_MASK   VARCHAR2(4000)  :=  P_FORMAT;',
'  BEGIN',
'    /*',
'    IF ',
'      INSTR(UPPER(L_ITEM_FORMAT_MASK),''DS'') > 0 ',
'      OR INSTR(UPPER(L_ITEM_FORMAT_MASK),''DL'') > 0 ',
'    THEN',
'      FOR L_FORMAT IN ( ',
'        SELECT ',
'          DS_FORMAT, ',
'          DL_FORMAT ',
'        FROM ',
'          WWV_FLOW_LANGUAGES ',
'        WHERE ',
'          LANG_ID_UPPER = UPPER(WWV_FLOW.G_BROWSER_LANGUAGE))',
'      LOOP',
'        L_ITEM_FORMAT_MASK := REGEXP_REPLACE(REGEXP_REPLACE(L_ITEM_FORMAT_MASK,''dl'',REPLACE(L_FORMAT.DL_FORMAT,''"'',''''),1,1,''i''),''ds'',REPLACE(L_FORMAT.DS_FORMAT,''"'',''''),1,1,''i'');',
'      END LOOP;',
'    END IF;',
'    */  ',
'    ',
'    IF INSTR(LOWER(TRIM(L_ITEM_FORMAT_MASK)),''fm'') = 1 THEN',
'        L_MONTH_FORMAT := ''m'';',
'        L_DAY_FORMAT := ''d'';',
'        L_DAY_OF_YEAR_FORMAT := ''o'';',
'    END IF;',
'    ',
'    L_ITEM_FORMAT_MASK := REGEXP_REPLACE(REGEXP_REPLACE(REGEXP_REPLACE(REGEXP_REPLACE(REGEXP_REPLACE(REGEXP_REPLACE(REGEXP_REPLACE(REGEXP_REPLACE(REGEXP_REPLACE(REGEXP_REPLACE(REGEXP_REPLACE(REGEXP_REPLACE(REGEXP_REPLACE(REGEXP_REPLACE(REGEXP_REPLACE'
||'(REGEXP_REPLACE(REGEXP_REPLACE(REGEXP_REPLACE(REGEXP_REPLACE(REGEXP_REPLACE',
'        (L_ITEM_FORMAT_MASK,''fm'','''',1,0,''i''),''D{3}'',L_DAY_OF_YEAR_FORMAT,1,0,''i''),''a\.?m\.?'',''%at%'',1,1,''i''),''p\.?m\.?'',''%pt%'',1,1,''i''),''HH'',''hh'',1,0),''hh24'',''HH'',1,0,''i''),''hh12'',''hh'',1,0,''i''),''MI'',''ii'',1,0,''i''),''SS'',''ss'',1,0,''i''),''D{2}'',L_DAY_FORMAT'
||',1,0,''i''),''M{2}'',L_MONTH_FORMAT,1,0,''i''),''Y{2}'',''y'',1,0,''i''),''month'',''MM'',1,0,''i''),''MON'',''M'',1,0,''i''),',
'        ''Y{4}'',''yy'',1,0,''i''),''R{2}'',''y'',1,0,''i''),''R{4}'',''yy'',1,0,''i''),''month'',''MM'',1,0,''i''),''day'',''DD'',1,0,''i''),''dy'',''D'',1,0,''i'');',
'    RETURN L_ITEM_FORMAT_MASK;',
'',
'  END GET_JAVASCRIPT_DATE_FORMAT;',
'',
'  function render (',
'    p_dynamic_action in apex_plugin.t_dynamic_action,',
'    p_plugin         in apex_plugin.t_plugin ',
'  ) return apex_plugin.t_dynamic_action_render_result ',
'  as',
'    v_result apex_plugin.t_dynamic_action_render_result;',
'    v_json varchar2(32000);',
'',
'    v_app_id number default v(''APP_ID'');',
'',
'    v_url varchar2(4000);',
'    --v_da_jQuery_selector varchar2(4000);',
'',
'    --v_attr_list_name              APEX_APPLICATION_PAGE_DA_ACTS.ATTRIBUTE_01%TYPE := p_dynamic_action.attribute_01;',
'    v_attr_type                   APEX_APPLICATION_PAGE_DA_ACTS.ATTRIBUTE_01%TYPE := p_dynamic_action.attribute_01;',
'    v_attr_lov_name               APEX_APPLICATION_PAGE_DA_ACTS.ATTRIBUTE_02%TYPE := p_dynamic_action.attribute_02;',
'    v_attr_action_type            APEX_APPLICATION_PAGE_DA_ACTS.ATTRIBUTE_03%TYPE := p_dynamic_action.attribute_03;',
'    v_attr_action_js_func         APEX_APPLICATION_PAGE_DA_ACTS.ATTRIBUTE_04%TYPE := p_dynamic_action.attribute_04;',
'    v_attr_action_process_name    APEX_APPLICATION_PAGE_DA_ACTS.ATTRIBUTE_05%TYPE := p_dynamic_action.attribute_05;',
'    v_attr_action_process_data    APEX_APPLICATION_PAGE_DA_ACTS.ATTRIBUTE_06%TYPE := p_dynamic_action.attribute_06;',
'    v_page_items_to_submit        APEX_APPLICATION_PAGE_DA_ACTS.ATTRIBUTE_07%TYPE := APEX_PLUGIN_UTIL.PAGE_ITEM_NAMES_TO_JQUERY( p_dynamic_action.attribute_07 );',
'    v_attr_ajax_success_callback  APEX_APPLICATION_PAGE_DA_ACTS.ATTRIBUTE_08%TYPE := p_dynamic_action.attribute_08;',
'    v_attr_ajax_error_callback    APEX_APPLICATION_PAGE_DA_ACTS.ATTRIBUTE_09%TYPE := p_dynamic_action.attribute_09;',
'    v_attr_ajax_settings          APEX_APPLICATION_PAGE_DA_ACTS.ATTRIBUTE_10%TYPE := p_dynamic_action.attribute_10;',
'    v_attr_date_format            APEX_APPLICATION_PAGE_DA_ACTS.ATTRIBUTE_11%TYPE := GET_JAVASCRIPT_DATE_FORMAT( p_dynamic_action.attribute_11 );',
'    v_attr_js_return_html         APEX_APPLICATION_PAGE_DA_ACTS.ATTRIBUTE_12%TYPE := p_dynamic_action.attribute_12;',
'    v_attr_settings               APEX_APPLICATION_PAGE_DA_ACTS.ATTRIBUTE_13%TYPE := p_dynamic_action.attribute_13;',
'    v_attr_compute_value_js_func  APEX_APPLICATION_PAGE_DA_ACTS.ATTRIBUTE_14%TYPE := p_dynamic_action.attribute_14;',
'    v_attr_item_attributes_json   APEX_APPLICATION_PAGE_DA_ACTS.ATTRIBUTE_15%TYPE := p_dynamic_action.attribute_15;',
'',
'    v_lov_entries apex_plugin_util.t_column_value_list;',
'',
'    v_lov_query APEX_APPLICATION_LOVS.LIST_OF_VALUES_QUERY%TYPE;',
'    v_lov_type  APEX_APPLICATION_LOVS.LOV_TYPE%TYPE;',
'',
'    e_process_not_found exception;',
'',
'    v_process_cnt number;',
'',
'    v_region_type     APEX_APPLICATION_PAGE_DA.WHEN_REGION%TYPE;',
'    v_region_id       APEX_APPLICATION_PAGE_DA.WHEN_REGION_ID%TYPE;',
'    ',
'    v_region_max_rows   APEX_APPLICATION_PAGE_REGIONS.MAXIMUM_ROWS_TO_QUERY%TYPE;',
'    v_pagination_scheme APEX_APPLICATION_PAGE_REGIONS.PAGINATION_SCHEME%TYPE;',
'    ',
'  begin',
'',
'    apex_javascript.add_library (',
'      p_name      => ''mustache'',',
'      p_directory => p_plugin.file_prefix',
'    );',
'',
'    apex_javascript.add_library (',
'      p_name      => ''pretius.apexInlineEdit'',',
'      p_directory => p_plugin.file_prefix',
'    );',
'',
'    apex_css.add_file (',
'      p_name => ''pretius.apexInlineEdit'',',
'      p_directory => p_plugin.file_prefix',
'     );',
'',
'    v_result.ajax_identifier     := wwv_flow_plugin.get_ajax_identifier;',
'    v_result.attribute_01        := v_attr_type;',
'    v_result.attribute_02        := v_attr_lov_name;',
'    v_result.attribute_03        := v_attr_action_type;',
'    v_result.attribute_04        := v_attr_action_js_func;',
'    v_result.attribute_05        := v_attr_action_process_name;',
'    v_result.attribute_06        := v_attr_action_process_data;',
'    v_result.attribute_07        := v_page_items_to_submit;',
'    v_result.attribute_08        := v_attr_ajax_success_callback;',
'    v_result.attribute_09        := v_attr_ajax_error_callback;',
'    v_result.attribute_10        := v_attr_ajax_settings;',
'    v_result.attribute_11        := v_attr_date_format;',
'    v_result.attribute_12        := v_attr_js_return_html;',
'    v_result.attribute_13        := v_attr_settings;',
'    v_result.attribute_14        := v_attr_compute_value_js_func;',
'    v_result.attribute_15        := v_attr_item_attributes_json;',
'    v_result.javascript_function := ''function(){}''; --overridden later',
'',
'    --pobierz informacje o typie raportu',
'    select ',
'      WHEN_REGION,',
'      WHEN_REGION_ID',
'      ',
'    into',
'      v_region_type,',
'      v_region_id',
'      ',
'    from ',
'      APEX_APPLICATION_PAGE_DA_ACTS aapda',
'    join',
'      APEX_APPLICATION_PAGE_DA aapd',
'    on',
'      aapd.DYNAMIC_ACTION_ID = aapda.DYNAMIC_ACTION_ID',
'    where ',
'      aapda.application_id = g_app_id',
'      and ACTION_ID = p_dynamic_action.id;',
'',
'    --pobierz informacje o paginacji',
'    select',
'      PAGINATION_SCHEME,',
'      MAXIMUM_ROWS_TO_QUERY',
'    into',
'      v_pagination_scheme,',
'      v_region_max_rows',
'    from ',
'      APEX_APPLICATION_PAGE_REGIONS ',
'    where ',
'      application_id = g_app_id',
'      and REGION_ID =  v_region_id;',
'',
'    if v_attr_action_type = ''PROCESS'' then',
'',
'      select ',
'        count(1)',
'      into',
'        v_process_cnt',
'      from ',
'        APEX_APPLICATION_PAGE_PROC ',
'      where ',
'        application_id = g_app_id',
'        and PROCESS_POINT_CODE = ''ON_DEMAND''',
'        and PROCESS_NAME = v_attr_action_process_name;',
'',
'      if v_process_cnt = 0 then',
'        raise e_process_not_found;',
'      end if;',
'',
'    end if;',
'',
'    --pobierz informacje o lov',
'    if v_attr_type = ''SELECT'' then',
'',
'      begin',
'        select',
'          LOV_TYPE,',
'          LIST_OF_VALUES_QUERY',
'        into',
'          v_lov_type,',
'          v_lov_query',
'        from ',
'          APEX_APPLICATION_LOVS',
'        where',
'          application_id = g_app_id',
'          and LIST_OF_VALUES_NAME = v_attr_lov_name;',
'      exception',
'        when no_data_found then',
'          raise;',
'      end;',
'',
'      if v_lov_type = ''Static'' then',
'        v_lov_query := ''',
'          select ',
'            DISPLAY_VALUE, ',
'            RETURN_VALUE ',
'          from ',
'            APEX_APPLICATION_LOV_ENTRIES ',
'          where ',
'            application_id = ''||g_app_id||''',
'            and LIST_OF_VALUES_NAME = ''||chr(39)||v_attr_lov_name||chr(39)||''',
'          order by',
'            DISPLAY_SEQUENCE',
'        '';',
'      end if;',
'',
'      apex_json.initialize_clob_output;',
'',
'      v_lov_entries := apex_plugin_util.get_data(',
'        p_sql_statement    => v_lov_query,',
'        p_min_columns      => 2,',
'        p_max_columns      => 2,',
'        p_component_name   => ''''',
'      );',
'',
'      apex_json.open_array;',
'',
'      for i in 1..v_lov_entries(1).count loop',
'        apex_json.open_object;',
'        apex_json.write(''d'', v_lov_entries(1)(i) );',
'        apex_json.write(''r'', v_lov_entries(2)(i) );',
'        apex_json.close_object;',
'      end loop;',
'',
'      apex_json.close_array;',
'      ',
'      v_json := apex_json.get_clob_output;',
'      apex_json.free_output;',
'    else ',
'      v_json := ''[]'';',
'    end if;',
'',
'    /*',
unistr('      do obs\0142u\017Cenia w IR aktualna liczba wierszy/paginacja etc'),
'    */',
'    --wykona sie raz',
'',
'    --APEX_JAVASCRIPT.ADD_ONLOAD_CODE (''alert(''||v_region_id||'')'');',
'',
'    v_result.javascript_function := ''',
'      function(){',
'        var debugName = ''''# ''''+this.action.action;',
'',
'        //apex.debug.log( debugName, ''''DA'''', ''''this'''', this );',
'        //console.log(''||to_char(v_region_id)||'');',
'',
'        $(this.triggeringElement).find( this.affectedElements ).apexInlineEdit( {',
'              "plugin": this,',
'              "lov": ''||v_json||'',',
'              "report": {',
'                "regionId": ''||v_region_id||'',                  //z jakiegos powodu zwraca zla wartosc - do sprwadzenia',
'                "paginationType": "''||v_pagination_scheme||''",',
'                "regionType": "''||v_region_type||''",',
'                "maxRows": ''||NVL(v_region_max_rows, -1)||''',
'              }              ',
'            }',
'        );',
'        ',
'        /*',
'        $(this.triggeringElement).find( this.affectedElements ).on(''''dblclick'''', $.proxy(function(pEvent){',
'          var self = $(pEvent.currentTarget);',
'',
'          if ( self.data(''''pretius-apexInlineEdit'''') == undefined ) {',
'            self.apexInlineEdit( {',
'              "plugin": this,',
'              "lov": ''||v_json||''',
'            } );  ',
'          }',
'          else {',
'            self.apexInlineEdit(''''show'''');',
'          }',
'          ',
'        }, this));',
'        */',
'',
'',
'        ',
'      }',
'    '';',
'',
'    return v_result;',
'',
'  exception',
'    when e_process_not_found then',
'      v_result.javascript_function := ''function(){ throw "# "+this.action.action+": AJAX Callback \"''||v_attr_action_process_name||''\" not found" }'';',
'      --apex_javascript.add_onload_code(''console.log( this )'');',
'      --apex_javascript.add_onload_code(''throw 1'');',
'      return v_result;',
